import { useState } from 'react'



const Button = (props) => {

  return (
    <button onClick={props.func}>{props.name}</button>
  )
}

const App = () => {
  const [ counter, setCounter ] = useState(0)

  const increaseCounter = () => {
    setCounter(counter + 1)
  }
  const resetCounter = () => {
    setCounter(0)
  }

  return (
    <div>
      <Button func={increaseCounter} name={"Click"}/>
      <Button func={resetCounter} name={"Reset"}/>
      <p>{counter}</p>
    </div>
  )
}

export default App
