import { useState } from 'react'

const IncreaseCount = (props) => {

  return (
    props.setState(props.counter + 1)
  )
}

const SetZero = props => {
  return (
    props.setState(0)
  )
}

const Button = (props) => {
  return (
    <button onClick={props.function}>{props.name}</button>
  )
}

const App = () => {
  const [ counter, setCounter ] = useState(0)

  return (
    <div>
      <Button setState={setCounter} counter={counter} function={IncreaseCount} name={"Click"}/>
      <Button setState={setCounter} function={SetZero} name={"Reset"}/>
      <p>{counter}</p>
    </div>
  )
}

export default App
