import { useState } from 'react'

const App = () => {
  const [goodValue, setGoodValue] = useState("")
  const [badValue, setBadValue] = useState("")
  const [neutralValue, setNeutralValue] = useState("")

  const setGood = () => {    
    setGoodValue(goodValue + 1)  
  }  

  const setBad = () => {    
    setBadValue(badValue + 1)  
  }  

  const setNeutral = () => {    
    setNeutralValue(neutralValue + 1)  
  }  

  const resetValues = () => {    
    setGoodValue(0)  
    setBadValue(0)  
    setNeutralValue(0)  
  }  



  return (
    <div>
      <h1>Give feedback</h1>
      <button onClick={setGood}>Good</button>      
      <button onClick={setBad}>Neutral</button>     
      <button onClick={setNeutral}>Bad</button>
      <h2>Statistics</h2>
      <p>Good {goodValue}</p>
      <p>Bad {badValue}</p>
      <p>Neutral {neutralValue}</p>
    </div>
  )
}

export default App
